import React, { useState, useEffect } from "react";

import { ContactForm } from "../../components/contactForm/ContactForm";
import { TileList } from "../../components/tileList/TileList";

export const ContactsPage = ({contactsList, addContact}) => {
  const phonePattern = new RegExp("^\d{10}$");
  const emailPattern = new RegExp("([!#-'*+/-9=?A-Z^-~-]+(\.[!#-'*+/-9=?A-Z^-~-]+)*|\"\(\[\]!#-[^-~ \t]|(\\[\t -~]))+\")@([!#-'*+/-9=?A-Z^-~-]+(\.[!#-'*+/-9=?A-Z^-~-]+)*|\[[\t -Z^-~]*])");
  /*
  Define state variables for 
  contact info and duplicate check
  */
 const [contactName, setContactName] = useState("");
 const [contactPhone, setContactPhone] = useState(0);
 const [contactEmail, setContactEmail] = useState("");


  const handleSubmit = (e) => {
    e.preventDefault();
    /*
    Add contact info and clear data
    if the contact name is not a duplicate
    */
   const duplicateName = (contact) => contact.name === contactName;
   if (!contactsList.some(duplicateName)) {
    addContact(contactName, contactPhone, contactEmail);
    e.target.value = '';
   }
  };

  /*
  Using hooks, check for contact name in the 
  contacts array variable in props
  */

  return (
    <div>
      <section>
        <h2>Add Contact</h2> 
      </section>
      <hr />
      <section>
        <h2>Contacts</h2>
      </section>
    </div>
  );
};
